function stripeHandlerToken(token) {
    firebase.firestore().collection('stripe_customers').doc(uid).collection('tokens').add({ token: token.id }).then(() => {
        this.newCreditCard = {
            number: '',
            cvc: '',
            exp_month: 1,
            exp_year: 2021,
            address_zip: ''
        };
    });
}

